import React, { Component } from 'react';
import parse from 'csv-parse';

const BACKGROUNDS = 'https://docs.google.com/spreadsheets/d/e/2PACX-1vQ-Gk5zIkpo2A9AwhSQKnCEprv-g2vk7vHP8qWxJFm-c6osL00jvjtR8gsCbxwlthulAJh3f5mXqSPk/pub?gid=153117320&single=true&output=csv';
const STARTING_GEAR = 'https://docs.google.com/spreadsheets/d/e/2PACX-1vQ-Gk5zIkpo2A9AwhSQKnCEprv-g2vk7vHP8qWxJFm-c6osL00jvjtR8gsCbxwlthulAJh3f5mXqSPk/pub?gid=1613277797&single=true&output=csv';
const NAMES = 'https://docs.google.com/spreadsheets/d/e/2PACX-1vQ-Gk5zIkpo2A9AwhSQKnCEprv-g2vk7vHP8qWxJFm-c6osL00jvjtR8gsCbxwlthulAJh3f5mXqSPk/pub?gid=674498928&single=true&output=csv';

const roll = (size) => Math.floor(Math.random() * size) + 1;

const pick = (array) => (
    array[Math.floor(Math.random() * array.length)]
);

const csvParser = (options = { columns: true, cast: true }) => (input) => new Promise((resolve, reject) => {
    parse(input, options, (err, output) => {
        if (err) return reject(err);
        resolve(output);
    });
});

const fetchCsvData = async (url) => {
    const data = await fetch(url);
    const text = await data.text();
    const parsedData = await csvParser()(text);

    return parsedData
}

const fetchData = async () => {
    const backgrounds = await fetchCsvData(BACKGROUNDS);
    const startingGear = await fetchCsvData(STARTING_GEAR);
    const names = await fetchCsvData(NAMES);

    return {
        backgrounds,
        startingGear,
        names,
    }
}

const splitRegExp = /(?![^)(]*\([^)(]*?\)\)),(?![^\(]*\))/;

const selectEquipment = (startingGear) => (r1, r2, r3, r4) => ([
    ...startingGear[r1 - 1]['Weapon'].split(splitRegExp),
    ...startingGear[r2 - 1]['Armour'].split(splitRegExp),
    ...startingGear[r3 - 1]['Item'].split(splitRegExp),
    `${r4} pennies`,
]);

class App extends Component {
    state = { givenName: '', familyName: '', items: [], failedCareer: null }

    componentDidMount() {
        fetchData().then(({ backgrounds, startingGear, names }) => {
            console.log(backgrounds, startingGear);

            const equipRoll1 = roll(10);
            const equipRoll2 = roll(10);
            const equipRoll3 = roll(10);
            const equipRoll4 = roll(10);

            const career = pick(backgrounds);

            const givenName =

            this.setState({
                failedCareer: career['Failed career'],
                items: [
                    career['Item 1'],
                    career['Item 2'],
                    '------',
                    `Equipment rolls: ${equipRoll1}, ${equipRoll2}, ${equipRoll3}, ${equipRoll4}`,
                    '------',
                    ...selectEquipment(startingGear)(equipRoll1, equipRoll2, equipRoll3, equipRoll4),
                ],
            });
        });
    }

    render() {
        const { items, failedCareer } = this.state;

        return (
            <div className="App">
                <h1>{failedCareer}</h1>
                <h2>{failedCareer}</h2>
                <ul>
                    {items.map((item, i) => (
                        <li key={i}>{item}</li>
                    ))}
                </ul>
            </div>
        );
    }
}

export default App;
